// fllowing 2 functions getRandomInt(min,max) and createRandomWordsString(dictionary) added by kashif
// generate random string of 4 words joining by hyphen from a dictionary

// Function to get a random integer between min and max (inclusive)
function getRandomInt(min, max) {
  return Math.floor(Math.random() * (max - min + 1)) + min;
}
// Function to create a string of 4 random unique words joined with a hyphen
function createRandomWordsString(dictionary) {
  const selectedWords = [];
  while (selectedWords.length < 4) {
    const randomIndex = getRandomInt(0, dictionary.length - 1);
    const word = dictionary[randomIndex];
    if (!selectedWords.includes(word)) {
      selectedWords.push(word);
    }
  }
  return selectedWords.join("-");
}


